define(["app/app"],function(e){"use strict";e.service("campaignsService",["$http","$q",function(e,n){this.getCampaigns=function(){var i=n.defer();return e.get("/api/campaigns").then(function(e){i.resolve(e)}),i.promise},this.setCampaign=function(n){console.log("Service received campaign selected: "+n),e.post("/api/setcampaign",{campaign:n}).success(function(e,n,i){console.log(e)}).error(function(e,n,i){console.log("failure message from Angular POST: "+JSON.stringify({data:e}))})}}])});